-- Criação do banco de dados
CREATE DATABASE IF NOT EXISTS FilmesDB;

-- Seleciona o banco de dados
USE FilmesDB;

-- Criação da tabela de filmes
CREATE TABLE IF NOT EXISTS filmes (
    id INT AUTO_INCREMENT PRIMARY KEY,     -- Identificador único
    titulo VARCHAR(255) NOT NULL,          -- Título do filme
    sinopse TEXT,                          -- Sinopse do filme
    capa VARCHAR(255),                     -- URL da capa do filme
    url_filme VARCHAR(255) NOT NULL,       -- URL do filme
    data_criacao TIMESTAMP DEFAULT CURRENT_TIMESTAMP -- Data de criação do registro
);


